mapper.insert:
insert into COMMENT (i_idx, m_idx, comment, post_date

        , comment_idx2

)
values (
        #{i_idx},
        #{m_idx},
        #{comment},
        now()

    , #{c_idx2}

       )

mapper.update:
update COMMENT
set comment = #{comment}
where comment_idx = #{c_idx} and m_idx = #{m_idx}

mapper.select:
select count(*)
from COMMENT
where comment_idx2 = #{c_idx}
