mapper.select:
select t.i_idx, t.m_idx, t.category_id, t.title, t.content, t.upload_date, t.last_updated_date, t.i_idx2, t.status from ( select inquiry_idx as i_idx, m_idx, category_id, title, content, date_format(upload_date, "%Y/%m/%d %H:%i:%s") as upload_date, date_format(last_updated_date, "%Y/%m/%d %H:%i:%s") as last_updated_date, inquiry_idx2 as i_idx2, case when inquiry_idx2 is null then inquiry_idx else inquiry_idx2 end as inq_group, i_status as status from INQUIRY ) t
where m_idx = #{m_idx}
order by inq_group desc, i_idx

mapper.insert:
insert into INQUIRY (m_idx, category_id, title, content
                     ,upload_date

                     ,inquiry_idx

                     )
values(#{m_idx}, #{category_id}, #{title}, #{content}
    ,now()

        ,#{i_idx2}

)
