mapper.select:
select t.i_idx, t.m_idx, t.category_id, t.content, t.image, t.image_db, t.upload_date, t.i_idx2 from ( select inquiry_idx as i_idx, m_idx, category_id, content, image, image_db, upload_date, inquiry_idx2 as i_idx2, case when inquiry_idx2 is null then inquiry_idx else inquiry_idx2 end as inq_group from INQUIRY ) t
where m_idx = #{m_idx}
order by inq_group desc, i_idx

mapper.insert:
insert into INQUIRY (m_idx, category_id, content

                         ,image, image_db

                     ,upload_date

                         ,inquiry_idx2

                     )
values(#{m_idx}, #{category_id}, #{content}

        ,#{image}, #{image_db}

    ,now()

        ,#{i_idx2}

)

mapper.update:
update INQUIRY

    category_id = #{category_id}
    ,content = #{content}

        ,image = #{image}, image_db = #{image_db}


where inquiry_idx = #{i_idx}
