mapper.select:
select *
from MEMBER
where userid = #{userid} and passwd = #{passwd}

mapper.update:
update MEMBER
set passwd = #{str}
where concat(EMAIL1,'@',EMAIL2)=#{userEmail} and userid = #{userid}

mapper.select:
select count(*)
from MEMBER
where userid = #{userid} and concat(EMAIL1,'@',EMAIL2)= #{userEmail}
