mapper.select:
select count(*)
from MEMBER
where userid=#{userid}

select m_idx, username, userid, passwd, email1, email2, authorities, image, image_db, phone1, phone2, phone3, DATE_FORMAT(joindate, '%Y-%m-%d') joindate
from MEMBER
WHERE m_idx=#{m_idx}

select *
from MEMBER
where userid = #{userid} and passwd = #{passwd}

mapper.update:
update MEMBER
set passwd = #{str}
where concat(EMAIL1,'@',EMAIL2)=#{userEmail} and userid = #{userid}

mapper.select:
select count(*)
from MEMBER
where userid = #{userid} and concat(EMAIL1,'@',EMAIL2)= #{userEmail}
